mapper.update:
UPDATE BOARDINFO
SET HITCOUNT=HITCOUNT+1
WHERE BOARDSEQ=#{boardseq}

update boardinfo
set BOARDTITLE=#{boardtitle}, BOARDCONTENT=#{boardcontent}
where boardseq=#{boardseq}

mapper.select:
select BOARDSEQ, ID, BOARDTITLE, BOARDCONTENT, HITCOUNT, INDATE
from boardinfo
where boardseq= #{boardseq}
